USE [x_LoadRLS1cUS]
GO
/*****************************************************************************************************************************************************/
IF OBJECT_ID           (N'[preVarNamesXLSX]', N'U') IS NOT NULL
DROP TABLE                [preVarNamesXLSX]
/*****************************************************************************************************************************************************/
SELECT * INTO             [preVarNamesXLSX]
/*****************************************************************************************************************************************************/
FROM
-------------------------------------------------------------------------------------------------------------------------------------------------------
/*****************************************************************************************************************************************************/
(                                                                                /*** parenthesis to include query                                 ***/
/*****************************************************************************************************************************************************/
    SELECT                                                                      /*** SELECT statement                                              ***/
            DISTINCT                                                            /*** should be distinct to include once each variable name         ***/
            D.[Question_abbreviation_std]                                       /*** Field including all Q Abb Std (StdVarName)                    ***/
          , Q.[Question_short_wording_std]                                      /*** Field including all short standard wordings                   ***/
    FROM                                                                        /*** from...                                                       ***/
              [LongData]                    D                                   /*** name of the table which includes long data                    ***/
            , [Pew_Question]                Q
   WHERE    D.[Question_abbreviation_std]                                       /*** FILTER when QAStd                                             ***/
          = Q.[Question_abbreviation_std]                                       /*** match any QAStd listed (StdVarName)                           ***/
/*****************************************************************************************************************************************************/
)                                                                     MyQ       /*** parenthesis to include query                                  ***/
